import React, { Component } from 'react';
import CardList from './CardList';
import { kanjiList } from './kanji';
import SearchBox from './SearchBox';





class App extends Component {
  constructor() {
    super();
    this.state = {
      kanjiList: kanjiList,
      searchField: ''
    }
  }

  searchChange = (event) => {
    const filteredKanji = this.state.kanjiList.filter(kanjiList => {
      return kanjiList.kunyomi.includes(this.state.searchField);
    })
    this.setState({searchField: event.target.value});
  }


  render() {
    return(
      <div className='tc '>
        <h1>Hello Welcome to Michael's 糞っぽい Kanji Dictionary</h1>
        <SearchBox searchChange={this.searchChange} />
        <CardList kanjiList={this.state.kanjiList}/>
      </div>
    );
  }


}

export default App;
