import React, { Component } from 'react';
import CardList from './CardList';
//import { kanjiList } from './kanji';
import SearchBox from './SearchBox';
import './App.css';


class App extends Component {
  constructor() {
    super();
    this.state = {
      kanjiList: [],
      searchField: ''
    }
  }

  searchChange = (event) => {
    this.setState({searchField: event.target.value});
  }

  componentDidMount() {
    fetch('https://raw.githubusercontent.com/jensechu/kanji/master/data/kanji.json').then(response => {
      return response.json();
    })
    .then(kanji => {
      this.setState({kanjiList: kanji}) 
    });

    console.log(kanjiList);

  }

  render() {
    const filteredKanji = this.state.kanjiList.filter(kanjiList => {
      return (kanjiList.kunyomi.includes(this.state.searchField) 
        || kanjiList.character.includes(this.state.searchField)
        || kanjiList.meaning.toLowerCase().includes(this.state.searchField))
    })

    return(
      <div className='tc '>
        <h1 className='f1'>Mikes Kanji Dictionary</h1>
        <SearchBox searchChange={this.searchChange} />
        <CardList kanjiList={filteredKanji}/>
      </div>
    );
  }


}

export default App;
